  var coordList = getTileCoords(req.x, req.y, req.z, underzoomLevels)
  // 遍历coordList
  async.each(
    coordList,
    function(coords, callback) {
      // clone一个新的请求
      var childReq = req.clone()
      childReq.x = coords[0]
      childReq.y = coords[1]
      childReq.z = coords[2]
      options.source.serve(server, childReq, function(
