  maxNativeZoom: 7,
  vectorTileLayerStyles: {
    // pre 是 -l 参数的图层名称
    pre: function (properties, zoom) {
      // 找到属性中对应降水量的属性设置图例
      const level = parseFloat(properties.title).toString();
      let color = "rgba(0, 0, 0, 0)";
      switch (level) {
        case "0.0001":
          color = "rgba(153,255,153,255)";
